import React, { Component } from 'react';

class DateOperations extends Component {

    state = {
        loading: false,
        startDate: '',
        endDate: '',
    };
    // Method called on click of submit button
    dateSubmitHandler = (event) => {
        // To prevent the form from submitting
        event.preventDefault();
    };
    inputChangedHandler = (event, inputIdentifier) => {
        if (inputIdentifier === 'startDate') {
            this.setState({startDate: event.target.value});
        } else {
            this.setState({endDate: event.target.value});
        }
        
    }
    render() {
        return <div>
            <form onSubmit={this.dateSubmitHandler}>
                <label>Start Date: </label> <input value= {this.state.startDate} onChange= {(event) => this.inputChangedHandler(event, 'startDate')}/>
                <br></br><br></br>
                <label>End Date: </label> <input value= {this.state.endDate} onChange= {(event) => this.inputChangedHandler(event, 'endDate')}/><br></br><br></br>
                <button onClick={this.dateSubmitHandler}>Submit</button>
            </form>
        </div>
    }
}

export default DateOperations;
