import React, { Component } from 'react';

import Input from '../components/UI/Input/Input';
import classes from './DateOperations.css';

class DateOperations extends Component {

    state = {
        loading: false,
        dateForm: {
            startDate: {
                elementLabel: 'Start Date',
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'DD/MM/YYYY'
                },
                value: '',
                validation: {
                    required: true
                },
                valid: false,
                touched: false,
            },
            endDate: {
                elementLabel: 'End Date',
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'DD/MM/YYYY'
                },
                value: '',
                validation: {
                    required: true
                },
                valid: false,
                touched: false
            }
        },
        isFormValid: false
    };
    // Method called on click of submit button
    dateSubmitHandler = (event) => {
        // To prevent the form from submitting
        event.preventDefault();
    };
    inputChangedHandler = (event, inputIdentifier) => {
        if (inputIdentifier === 'startDate') {
            this.setState({ startDate: event.target.value });
        } else {
            this.setState({ endDate: event.target.value });
        }

    }
    render() {
        const formElementsArray = [];
        for (let key in this.state.dateForm) {
            formElementsArray.push({
                id: key,
                config: this.state.dateForm[key]
            });
        }
        let form = (
            <form onSubmit={this.dateSubmitHandler}>
                {formElementsArray.map(formElement => {
                    <Input
                        key= {formElement.id}
                        elementType = {formElement.config.elementType}
                        elementConfig = {formElement.config.elementConfig}
                        value = {formElement.config.value}
                        invalid = {!formElement.config.valid}
                        touched={formElement.config.touched}
                        changed={(event) => this.inputChangedHandler(event, formElement.id)}/>
                })}
                <button onClick={this.dateSubmitHandler} disabled= {!this.state.isFormValid}>Submit</button>
            </form>
        );
        return <div className={classes.DateOperations}>
            <form onSubmit={this.dateSubmitHandler}>
                <label>Start Date: </label>
                <input
                    value={this.state.startDate}
                    placeholder="DD/MM/YYYY"
                    onChange={(event) => this.inputChangedHandler(event, 'startDate')} />
                <br></br><br></br>
                <label>End Date: </label>
                <input value={this.state.endDate}
                    placeholder="DD/MM/YYYY"
                    onChange={(event) => this.inputChangedHandler(event, 'endDate')} />
                <br></br><br></br>
                
            </form>
        </div>
    }
}

export default DateOperations;
